import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Header = ({ user }) => {
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  return (
    <header style={styles.header}>
      <h1 style={styles.logo}>Plataforma de Cursos</h1>
      <div style={styles.userMenu}>
        <button
          style={styles.dropdownButton}
          onClick={() => setMenuOpen(!menuOpen)}
        >
          {user.name}
        </button>
        {menuOpen && (
          <ul style={styles.dropdownMenu}>
            <li onClick={() => navigate("/account")}>Configuración de cuenta</li>
            <li onClick={handleLogout}>Cerrar sesión</li>
          </ul>
        )}
      </div>
    </header>
  );
};

const styles = {
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "10px 20px",
    backgroundColor: "#282c34",
    color: "#fff",
  },
  logo: {
    margin: 0,
  },
  userMenu: {
    position: "relative",
  },
  dropdownButton: {
    background: "none",
    border: "none",
    color: "#fff",
    fontSize: "16px",
    cursor: "pointer",
  },
  dropdownMenu: {
    position: "absolute",
    top: "100%",
    right: 0,
    backgroundColor: "#fff",
    color: "#000",
    listStyle: "none",
    margin: 0,
    padding: "10px",
    borderRadius: "5px",
    boxShadow: "0px 4px 6px rgba(0,0,0,0.1)",
    cursor: "pointer",
  },
};

export default Header;
