import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login';
// import RecordingsDashboard from './pages/RecordingsDashboard';
import StudentRecordings from './pages/StudentRecordings';
import AdminDashboard from './pages/AdminDashboard';
import ManageUsers from './pages/ManageUsers';
import ManageRecordings from './pages/ManageRecordings';
import Account from "./pages/Account"; // Configuración de cuenta
import { jwtDecode } from "jwt-decode";

const ProtectedRoute = ({ requiredRole, children }) => {
  const user = JSON.parse(localStorage.getItem('user')); // Obtener usuario desde localStorage

  if (!user) {
    // Si no hay usuario autenticado, redirige al login
    return <Navigate to="/" />;
  }

  if (!requiredRole.includes(user.role)) {
    // Si el rol no está permitido, redirige al dashboard adecuado
    return user.role === 'recordings' ? (
      <Navigate to="/student" />
    ) : (
      <Navigate to="/" />
    );
  }

  return children; // Renderizar contenido si cumple con los roles permitidos
};

const App = () => {
  const token = localStorage.getItem('token'); // Revisa si el usuario está autenticado
  const user = token ? jwtDecode(token) : null;

  return (
    <Router>

      <Routes>
        {/* Ruta pública */}
        <Route path="/" element={<Login />} />
        {/* <Route path="/login" element={!user ? <Login /> : <Navigate to="/" />} /> */}

        {/* Rutas para estudiantes */}
        <Route
          path="/recordings"
          element={
            <ProtectedRoute requiredRole="student">
              <StudentRecordings user={user}/>
            </ProtectedRoute>
          }
        />
        <Route path="/account" element={user ? <Account user={user} /> : <Navigate to="/login" />} />
        {/* Rutas para administradores */}
        <Route
          path="/admin"
          element={
            <ProtectedRoute requiredRole="admin">
              <AdminDashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/users"
          element={
            <ProtectedRoute requiredRole="admin">
              <ManageUsers />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/recordings"
          element={
            <ProtectedRoute requiredRole="admin">
              <ManageRecordings />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
