import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './pages/Login';
// import RecordingsDashboard from './pages/RecordingsDashboard';
import StudentRecordings from './pages/StudentRecordings';
import AdminDashboard from './pages/AdminDashboard';
import ManageUsers from './pages/ManageUsers';
import ManageRecordings from './pages/ManageRecordings';
import Account from "./pages/Account"; // Configuración de cuenta



const ProtectedRoute = ({ children, requiredRole }) => {
  const role = localStorage.getItem('role');
  const token = localStorage.getItem('token');

  if (!token || role !== requiredRole) {
    return <Navigate to="/" />;
  }

  return children;
};

const App = () => {
  const token = localStorage.getItem('token'); // Revisa si el usuario está autenticado
  const user = token ? jwtDecode(token) : null;

  return (
    <Router>
      {user && <Header user={user} />}
      <Routes>
        {/* Ruta pública */}
        {/* <Route path="/" element={<Login />} /> */}
        <Route path="/login" element={!user ? <Login /> : <Navigate to="/" />} />

        {/* Rutas para estudiantes */}
        <Route
          path="/recordings"
          element={
            <ProtectedRoute requiredRole="student">
              <StudentRecordings />
            </ProtectedRoute>
          }
        />
        <Route path="/account" element={user ? <Account /> : <Navigate to="/login" />} />
        {/* Rutas para administradores */}
        <Route
          path="/admin"
          element={
            <ProtectedRoute requiredRole="admin">
              <AdminDashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/users"
          element={
            <ProtectedRoute requiredRole="admin">
              <ManageUsers />
            </ProtectedRoute>
          }
        />
        <Route
          path="/admin/recordings"
          element={
            <ProtectedRoute requiredRole="admin">
              <ManageRecordings />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
