import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ManageRecordings = () => {
    const [recordings, setRecordings] = useState([]);
    const [error, setError] = useState('');
    const [newRecording, setNewRecording] = useState({
        title: '',
        description: '',
        url: '',
      });
    const [editingId, setEditingId] = useState(null);
    const [editingData, setEditingData] = useState({ title: '', description: '' });

    const fetchRecordings = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:5000/api/recordings', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setRecordings(response.data);
        } catch (err) {
            setError('No se pudieron cargar las grabaciones.');
        }
    };

    const handleAddRecording = async () => {
        try {
          const token = localStorage.getItem('token');
          await axios.post('http://localhost:5000/api/recordings', newRecording, {
            headers: { Authorization: `Bearer ${token}` },
          });
          fetchRecordings();
          setNewRecording({ title: '', description: '', url: '' });
        } catch (error) {
          console.error('Error adding recording:', error);
        }
      };

    const handleEdit = (recording) => {
        setEditingId(recording._id);
        setEditingData({ title: recording.title, description: recording.description });
    };

    const handleSaveEdit = async (id) => {
        try {
            const token = localStorage.getItem('token');
            await axios.put(
                `http://localhost:5000/api/recordings/${id}`,
                editingData,
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );
            setEditingId(null);
            fetchRecordings();
        } catch (err) {
            setError('Error al actualizar la grabación.');
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm('¿Estás seguro de que deseas eliminar esta grabación?')) return;

        try {
            const token = localStorage.getItem('token');
            await axios.delete(`http://localhost:5000/api/recordings/${id}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            fetchRecordings();
        } catch (err) {
            setError('Error al eliminar la grabación.');
        }
    };

    useEffect(() => {
        fetchRecordings();
    }, []);

    return (
        <>
          <div className="manage-recordings">
            <h1>Gestionar Grabaciones</h1>
            {error && <p className="error">{error}</p>}
            <table>
                <thead>
                    <tr>
                        <th>Título</th>
                        <th>Descripción</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {recordings.map((recording) => (
                        <tr key={recording._id}>
                            <td>
                                {editingId === recording._id ? (
                                    <input
                                        type="text"
                                        value={editingData.title}
                                        onChange={(e) =>
                                            setEditingData({ ...editingData, title: e.target.value })
                                        }
                                    />
                                ) : (
                                    recording.title
                                )}
                            </td>
                            <td>
                                {editingId === recording._id ? (
                                    <input
                                        type="text"
                                        value={editingData.description}
                                        onChange={(e) =>
                                            setEditingData({ ...editingData, description: e.target.value })
                                        }
                                    />
                                ) : (
                                    recording.description
                                )}
                            </td>
                            <td>
                                {editingId === recording._id ? (
                                    <>
                                        <button onClick={() => handleSaveEdit(recording._id)}>
                                            Guardar
                                        </button>
                                        <button onClick={() => setEditingId(null)}>Cancelar</button>
                                    </>
                                ) : (
                                    <>
                                        <button onClick={() => handleEdit(recording)}>Editar</button>
                                        <button onClick={() => handleDelete(recording._id)}>Eliminar</button>
                                    </>
                                )}
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
        </>
    );
};

export default ManageRecordings;
