import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../css/account.css'

const Account = () => {
  const [user, setUser] = useState({
    name: '',
    lastName: '',
    email: '',
    phone: '',
    dateOfBirth: '',
  });


  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/users/me', {
          headers: { Authorization: `Bearer ${token}` },
        });

        const userData = response.data;

        if (userData.dateOfBirth) {
          userData.dateOfBirth = new Date(userData.dateOfBirth)
            .toISOString()
            .split('T')[0];

        }
        setUser(response.data); // Ajustar según el formato de la respuesta
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  return (
    <section className='account'>
      <Link to="/admin/">Regresar a grabaciones</Link>
      <h1>Cuenta: {user.name} {user.lastName}</h1>
      <form className='account__info frame'>
        <h2>Información personal</h2>
        <p className='info-text'>Aquí se mostrará su información personal. Si necesita alguna corrección póngase en contacto con el centro de ayuda</p>
        <div className='input-group'>
          <label>Nombre</label>
          <input
            type="text"
            name="name"
            value={user.name || ''}
            readOnly
          />
        </div>
        <div className="input-group">
          <label>Apellidos</label>
          <input
            type="text"
            name="lastName"
            value={user.lastName || ''}
            readOnly
          />
        </div>
        <div className='input-group'>
          <label>Correo Electrónico</label>
          <input
            type="email"
            name="email"
            value={user.email || ''}
            readOnly
          />
        </div>
        <div className="input-group">
          <label>Teléfono</label>
          <input
            type="text"
            name="phone"
            value={user.phone || ''}
            readOnly
          />
        </div>
        <div className="input-group">
          <label>Fecha de Nacimiento</label>
          <input
            type="date"
            name="birthDate"
            value={user.dateOfBirth || ''}
            readOnly
          />
        </div>

      </form>
      <form className='account__resetPass frame'>
        <h2>Cambiar contraseña</h2>
        <p className='info-text'>Se recomienda cambiar la contraseña que recibió inicialmente por una personalizada</p>
        <label htmlFor="currentPass">Contraseña actual</label>
        <input type="password" name="currentPass" id="currentPass" />
        <div className='double-input'>
          <div className='input-group'>
            <label htmlFor="newPass">Nueva contraseña</label>
            <input type="password" name="newPass" id="newPass" />
          </div>
          <div className='input-group'>
            <label htmlFor="confirmNewPass">Confirmar contraseña nueva</label>
            <input type="password" name="confirmNewPass" id="confirmNewPass" />
          </div>
        </div>
        <button>Guardar</button>
      </form>
    </section>
  );
};

export default Account;
